import React from 'react'

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      company: []
    }
  }
render() {
  return (
    <div className="App">
      <Table companies={this.state.company}/>
    </div>
  );
}
componentDidMount() {
  fetch('https://prueba6.herokuapp.com/companies')
  .then(res => res.json())
  .then(json => json.company)
  .then(company => this.setState({ 'company': company }))
}
}

const Table = ({ companies }) => {
    return (
      <table>
        <thead>
          <tr>
            <th>Id</th>
            <th>Name</th>
            <th>url</th>
          </tr>
        </thead>
        <tbody>
          { (App.length > 0) ? companies.map( (company, index) => {
             return (
              <tr key={ index }>
                <td>{ company._id }</td>
                <td>{ company.name }</td>
                <td>{ company.homepage_url}</td>
              </tr>
            )
           }) : <tr><td colSpan="5">Loading...</td></tr> }
        </tbody>
      </table>
    );
  }
export default Table;
