import React, { Component } from 'react';

class Create extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: '',
      homepage_url: '',
    }
  }

  handleChange = (e) => {
    const target = e.target;
    const name = target.name;
    const value = target.value;

    this.setState({
      [name]: value,
    })
  }
  handleClick() {
    console.log('this is:', this);
  }

  handleSubmit = (e) => {
    e.preventDefault();

    fetch(`https://prueba6.herokuapp.com/companies?access_token=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjVjYTVmZTAxNWQxMGJlMDAwNDA1ZDU0MiIsImlhdCI6MTU1NDM4MjMzN30.vIlV2FVQ1ZJ0LibsMYdlsnrsUznWZrAAgBAd5A8jpKs`)
    .then(response => {
      console.log(response);
      return response.json()
   })
  }

  render() {
    const { name, homepage_url, products } = this.state;

    return (
      <form onSubmit={this.handleSubmit}>
        <input
          name="name"
          onChange={this.handleChange}
          placeholder="name"
          value={name}
        />
        <input
          name="homepage_url"
          onChange={this.handleChange}
          placeholder="homepage_url"
          value={homepage_url}
        />
        <input
          name="Products"
          onChange={this.handleChange}
          placeholder="Products"
          value={products}
        />
        <p>{homepage_url}</p>
        <button>Submit</button>
      </form>
    );
  }
}

export default Create;
