import React, { Component }  from 'react';
import Table from './components/table.js';

class App extends Component {
    constructor(props) {
      super(props);
      this.state = {
        company: [],
        isLoading: false,
        error: null,
      }
    }
  render() {
    return (
      <div className="tabla">
        <Table company={this.state.company}/>
      </div>
    );
  }
  componentDidMount(){
    this.setState({
       isLoading: true
      })
    fetch('https://prueba6.herokuapp.com/companies/?access_token=eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjVjYTVmZTAxNWQxMGJlMDAwNDA1ZDU0MiIsImlhdCI6MTU1NDM4MjMzN30.vIlV2FVQ1ZJ0LibsMYdlsnrsUznWZrAAgBAd5A8jpKs')
    .then(response => {
         if(response.ok){
         return response.json()
        }else{
        throw new Error('Something went wrong...')
      }
   })
    .then(data => this.setState({
      companies: data.companies,
      isLoading: false
    }))
    .catch(error => this.setState({
       error: null, 
       isLoading: false
    }))
   }

}

export default App;
